import tkinter as tk
import base64 as b64

## init the window
window=tk.Tk()
window.title("image test")
window.geometry("450x115")

## make the image
img = tk.PhotoImage(data=b64.b64decode(""))

## make the button
btn = tk.Button(window, command=lambda: print("button clicked"))
btn.config(image=img)
print(btn.cget("height")
      ,btn.cget("width"))
btn.pack()

## run the window
window.mainloop()